package com.rssoft.restapi.entities;import jakarta.persistence.Entity;import jakarta.persistence.Id;@Entitypublic class Course {    @Id    private int id;    private String CourseName;    private long price;    public Course() {    }    public Course(int id, String courseName, long price) {        this.id = id;        CourseName = courseName;        this.price = price;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getCourseName() {        return CourseName;    }    public void setCourseName(String courseName) {        CourseName = courseName;    }    public long getPrice() {        return price;    }    public void setPrice(long price) {        this.price = price;    }}